import React, { useState, useEffect } from "react";
import { motion } from "framer-motion";

const Contact = () => {
  const [formData, setFormData] = useState({
    name: "",
    mobile: "",
    email: "",
    message: "",
  });

  const [status, setStatus] = useState("");

  // YOUR NEW WORKING CREDENTIALS
  const EMAILJS_KEY      = "LdiT36053mllantaE";
  const EMAILJS_SERVICE  = "service_fxj2y8h";
  const EMAILJS_TEMPLATE = "template_9lqtcc5";

  // FIXED — ALWAYS INITIALIZES EMAILJS PROPERLY
  useEffect(() => {
    const script = document.createElement("script");
    script.src = "https://cdn.jsdelivr.net/npm/@emailjs/browser@4/dist/email.min.js";
    script.async = true;

    script.onload = () => {
      if (window.emailjs) {
        window.emailjs.init(EMAILJS_KEY);
        console.log("EmailJS Loaded & Initialized!");
      }
    };

    document.body.appendChild(script);
  }, []);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setStatus("sending");

    try {
      await window.emailjs.send(
        EMAILJS_SERVICE,
        EMAILJS_TEMPLATE,
        {
          from_name: formData.name,
          from_email: formData.email,
          phone: formData.mobile,
          message: formData.message,
        }
      );

      setStatus("success");
      setFormData({ name: "", mobile: "", email: "", message: "" });
    } catch (err) {
      console.error("Send failed:", err);
      setStatus("error");
    }

    setTimeout(() => setStatus(""), 4000);
  };

  return (
    <section id="contact" className="py-12 sm:py-20 bg-navy-50">
      <div className="px-4 mx-auto max-w-7xl">
        <motion.h2
          className="mb-8 text-3xl font-bold text-center sm:text-5xl sm:mb-12 text-navy-800"
          initial={{ y: -50 }}
          whileInView={{ y: 0 }}
          viewport={{ once: true }}
        >
          Contact Us
        </motion.h2>

        <motion.div
          className="grid grid-cols-1 gap-6 md:grid-cols-2 md:gap-12"
          initial={{ opacity: 0 }}
          whileInView={{ opacity: 1 }}
          viewport={{ once: true }}
        >

          {/* Contact Details */}
          <div className="space-y-4 text-navy-700">
            <h3 className="text-xl font-bold sm:text-2xl">Details</h3>

            <p><strong>Email:</strong> indiaadvertising05@gmail.com</p>
          </div>

          {/* Form */}
          <form onSubmit={handleSubmit} className="space-y-4">
            <input type="text" name="name" placeholder="Name" value={formData.name} onChange={handleChange} required />

            <input type="tel" name="mobile" placeholder="Mobile" value={formData.mobile} onChange={handleChange} required />

            <input type="email" name="email" placeholder="Email" value={formData.email} onChange={handleChange} required />

            <textarea name="message" placeholder="Message" rows="4" value={formData.message} onChange={handleChange} required />

            <motion.button
              type="submit"
              disabled={status === "sending"}
              className="w-full py-4 font-bold bg-yellow-500"
            >
              {status === "sending" ? "Sending…" : "Send Message"}
            </motion.button>

            {status === "success" && (
              <div className="p-4 text-green-600">Message sent!</div>
            )}

            {status === "error" && (
              <div className="p-4 text-red-600">Failed to send. Try again.</div>
            )}
          </form>
        </motion.div>
      </div>
    </section>
  );
};

export default Contact;
