// src/components/Navbar.jsx
import React, { useState, useEffect } from "react";
import { motion, AnimatePresence } from "framer-motion";
import logo from "../assets/indiaadvertising-logo.png";
import { Menu, X, ChevronDown } from "lucide-react";
import companyData from "../data/companyData.json";

const Navbar = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [activeSection, setActiveSection] = useState("home");
  const [showProductsDropdown, setShowProductsDropdown] = useState(false);

  const categories = companyData.categories || [];

  const navItems = [
    { name: "Home", id: "home" },
    { name: "About", id: "about" },
    { name: "Products", id: "products", hasDropdown: true },
    { name: "Gallery", id: "gallery" },
    { name: "Contact", id: "contact" },
  ];

  const scrollToSection = (id) => {
    setIsOpen(false);
    setShowProductsDropdown(false);
    const el = document.getElementById(id);
    if (el) el.scrollIntoView({ behavior: "smooth" });
  };

  // Scroll Spy
  useEffect(() => {
    const handleScroll = () => {
      let current = "home";
      navItems.forEach((item) => {
        const section = document.getElementById(item.id);
        if (section) {
          const rect = section.getBoundingClientRect();
          if (rect.top <= 120 && rect.bottom >= 120) current = item.id;
        }
      });
      setActiveSection(current);
    };
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  return (
    <motion.nav className="fixed top-0 left-0 right-0 z-50 bg-white border-b shadow-sm">
      <div className="flex items-center justify-between h-16 px-4 mx-auto max-w-7xl">

        {/* Logo */}
        <button onClick={() => scrollToSection("home")}>
          <img src={logo} className="h-10" alt="Logo" />
        </button>

        {/* Desktop Menu */}
        <div className="items-center hidden gap-8 md:flex">
          {navItems.map((item) => (
            <div key={item.id} className="relative">
              <button
                onClick={() => !item.hasDropdown && scrollToSection(item.id)}
                onMouseEnter={() => item.hasDropdown && setShowProductsDropdown(true)}
                className={`text-base font-medium transition pb-1 border-b-2 ${
                  activeSection === item.id ? "border-blue-700 text-blue-700" : "border-transparent"
                }`}
              >
                {item.name}
              </button>

              {item.hasDropdown && (
                <ChevronDown
                  size={16}
                  className={`inline ml-1 transition ${showProductsDropdown ? "rotate-180" : ""}`}
                />
              )}
            </div>
          ))}
        </div>

        {/* Mobile Menu Icon */}
        <button className="md:hidden" onClick={() => setIsOpen(!isOpen)}>
          {isOpen ? <X size={26} /> : <Menu size={26} />}
        </button>
      </div>

      {/* Products Dropdown */}
      <AnimatePresence>
        {showProductsDropdown && (
          <motion.div
            initial={{ opacity: 0, y: -10 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: -10 }}
            onMouseLeave={() => setShowProductsDropdown(false)}
            className="absolute left-0 w-full bg-white border-t shadow-md top-16"
          >
            <div className="grid grid-cols-2 gap-6 p-6 mx-auto max-w-7xl sm:grid-cols-3 lg:grid-cols-5">
              {categories.map((cat) => {
                const id = cat.category.toLowerCase().replace(/\s+/g, "-");
                return (
                  <button
                    key={cat.category}
                    onClick={() => scrollToSection(id)}
                    className="p-4 transition border rounded-xl hover:bg-blue-50 hover:text-blue-700"
                  >
                    <p className="font-semibold">{cat.category}</p>
                    <p className="text-sm text-gray-500">{cat.subProducts?.length || 0} Products</p>
                  </button>
                );
              })}
            </div>
          </motion.div>
        )}
      </AnimatePresence>

      {/* Mobile Slide Menu */}
      <AnimatePresence>
        {isOpen && (
          <motion.div
            initial={{ x: "100%" }}
            animate={{ x: 0 }}
            exit={{ x: "100%" }}
            className="fixed top-0 right-0 h-full p-6 bg-white shadow-xl w-80 md:hidden"
          >
            <div className="flex flex-col gap-4 mt-10">
              {navItems.map((item) => (
                <button
                  key={item.id}
                  onClick={() => scrollToSection(item.id)}
                  className="py-2 text-lg text-gray-700 border-b"
                >
                  {item.name}
                </button>
              ))}

              <button
                onClick={() => scrollToSection("contact")}
                className="w-full py-3 mt-6 text-white bg-blue-600 rounded-lg"
              >
                Contact Us
              </button>
            </div>
          </motion.div>
        )}
      </AnimatePresence>
    </motion.nav>
  );
};

export default Navbar;
