import React, { useState, useRef, useMemo, useEffect, useCallback } from "react";
import { motion } from "framer-motion";
import { ChevronLeft, ChevronRight, Search, X } from "lucide-react";
import "./Products.css";
import localData from "../data/companyData.json";

const Products = () => {
  const [data, setData] = useState(localData);
  const [loading, setLoading] = useState(false);
  const [activeCategory, setActiveCategory] = useState(null);
  const [searchTerm, setSearchTerm] = useState("");
  const [compareProducts, setCompareProducts] = useState([]);
  const scrollRef = useRef(null);
  const [showForm, setShowForm] = useState(false);
  const [showDetails, setShowDetails] = useState(false);
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [selectedForDetails, setSelectedForDetails] = useState(null);

  useEffect(() => {
    setData(localData);
    setLoading(false);
  }, []);

  const categories = useMemo(
    () =>
      data?.categories?.map((cat) => ({
        ...cat,
        id: cat.category.toLowerCase().replace(/\s+/g, "-"), // üî• Auto-ID for scroll
        subProducts: (cat.subProducts || []).filter((prod) => prod != null),
      })) || [],
    [data]
  );

  const { description = "" } = data?.companyInfo || {};

  const scroll = useCallback((direction) => {
    if (scrollRef.current) {
      scrollRef.current.scrollBy({
        left: direction === "left" ? -300 : 300,
        behavior: "smooth",
      });
    }
  }, []);

  const filteredCategories = useMemo(
    () =>
      categories.filter((p) =>
        p.category?.toLowerCase().includes(searchTerm.toLowerCase())
      ),
    [categories, searchTerm]
  );

  const allCategoryNames = useMemo(
    () => filteredCategories.map((c) => c.category),
    [filteredCategories]
  );

  const activeSubProducts = useMemo(() => {
    if (!activeCategory) return [];
    return categories.find((p) => p.category === activeCategory)?.subProducts || [];
  }, [categories, activeCategory]);

  const scrollToCategory = (id) => {
    setTimeout(() => {
      const el = document.getElementById(id);
      if (el) el.scrollIntoView({ behavior: "smooth", block: "start" });
    }, 150);
  };

  const toggleCompare = (product) => {
    setCompareProducts((prev) => {
      const exists = prev.some((p) => p.name === product.name);
      if (exists) return prev.filter((p) => p.name !== product.name);
      if (prev.length < 4) return [...prev, product];
      return prev;
    });
  };

  const handleViewAllProducts = (category, id) => {
    setActiveCategory(category);
    scrollToCategory(id);
  };

  const getImageUrl = (img) => {
    if (!img) return null;
    if (img.startsWith("http") || img.startsWith("/")) return img;
    let p = img.replace(/^uploads\//, "images/");
    return `${process.env.PUBLIC_URL}/${p}`;
  };

  if (loading) return <div className="py-12 text-center">Loading products...</div>;

  return (
    <section id="products" className="py-12 text-gray-800 bg-gray-50 sm:py-20">
      <div className="px-4 mx-auto max-w-7xl">
        <motion.h2 className="mb-6 text-3xl font-bold text-center text-blue-700 sm:text-5xl">
          Our Products & Services
        </motion.h2>

        <p className="max-w-3xl mx-auto mb-8 text-lg text-center text-gray-600">
          {description}
        </p>

        {/* üîç Search */}
        <div className="flex items-center gap-4 mb-8">
          <div className="flex-1 max-w-md">
            <div className="relative">
              <Search className="absolute text-gray-400 -translate-y-1/2 left-3 top-1/2" size={20} />
              <input
                type="text"
                placeholder="Search categories..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="w-full py-3 pl-10 pr-4 transition-all border-2 border-gray-300 rounded-lg focus:border-blue-600"
              />
            </div>
          </div>

          {activeCategory && (
            <button
              onClick={() => setActiveCategory(null)}
              className="px-6 py-3 font-semibold text-white bg-gray-600 rounded-lg"
            >
              ‚Üê All Categories
            </button>
          )}
        </div>

        {/* üîµ Scrollable category chips */}
        <div className="relative mb-10">
          <button
            onClick={() => scroll("left")}
            className="absolute left-0 p-2 text-blue-600 -translate-y-1/2 rounded-full top-1/2"
          >
            <ChevronLeft size={24} />
          </button>

          <div ref={scrollRef} className="flex gap-3 px-10 overflow-x-auto no-scrollbar">
            <button
              onClick={() => setActiveCategory(null)}
              className={`px-6 py-3 rounded-full font-semibold ${
                !activeCategory ? "bg-blue-600 text-white" : "bg-white text-blue-700 border border-blue-300"
              }`}
            >
              All Categories
            </button>

            {filteredCategories.map((cat) => (
              <button
                key={cat.id}
                onClick={() => {
                  setActiveCategory(cat.category);
                  scrollToCategory(cat.id);
                }}
                className={`px-6 py-3 rounded-full font-semibold ${
                  activeCategory === cat.category
                    ? "bg-blue-600 text-white"
                    : "bg-white text-blue-700 border border-blue-300"
                }`}
              >
                {cat.category}
              </button>
            ))}
          </div>

          <button
            onClick={() => scroll("right")}
            className="absolute right-0 p-2 text-blue-600 -translate-y-1/2 rounded-full top-1/2"
          >
            <ChevronRight size={24} />
          </button>
        </div>

        {/* üü¶ Categories + Products */}
        <div className="grid gap-6 lg:grid-cols-4">
          {!activeCategory ? (
            filteredCategories.map((cat) => (
              <div id={cat.id} key={cat.id}>
                <FeaturedCategoryCard
                  category={cat.category}
                  subProducts={cat.subProducts}
                  imageSrc={cat.subProducts[0]?.images?.[0] ? getImageUrl(cat.subProducts[0]?.images?.[0]) : null}
                  onViewAll={() => handleViewAllProducts(cat.category, cat.id)}
                />
              </div>
            ))
          ) : (
            <div className="lg:col-span-4">
              <h2 id={categories.find((c) => c.category === activeCategory)?.id} className="mb-4 text-3xl font-bold text-blue-700">
                {activeCategory}
              </h2>

              <div className="grid gap-6 lg:grid-cols-3">
                {activeSubProducts.map((sub, idx) => (
                  <ProductCard
                    key={idx}
                    name={sub.name}
                    price={sub.price}
                    images={sub.images}
                    description={sub.description}
                    specs={sub.specs || []}
                    additionalInfo={sub.additionalInfo || {}}
                    onQuote={() => {
                      setSelectedProduct(sub);
                      setShowForm(true);
                    }}
                    onDetails={() => {
                      setSelectedForDetails(sub);
                      setShowDetails(true);
                    }}
                    onCompare={() => toggleCompare(sub)}
                    isCompared={compareProducts.some((p) => p.name === sub.name)}
                    getImageUrl={getImageUrl}
                  />
                ))}
              </div>
            </div>
          )}
        </div>

        {/* Compare Panel */}
        {compareProducts.length > 0 && (
          <ComparePanel products={compareProducts} onRemove={toggleCompare} getImageUrl={getImageUrl} />
        )}

        {/* Custom Solution Button */}
        <div className="mt-12 text-center">
          <button
            onClick={() => {
              setSelectedProduct({ name: "Custom Solution" });
              setShowForm(true);
            }}
            className="px-8 py-4 text-lg font-semibold text-white bg-blue-600 rounded-full"
          >
            Enquire for Custom Solutions
          </button>
        </div>
      </div>

      {showForm && <QuoteForm product={selectedProduct} onClose={() => setShowForm(false)} />}
      {showDetails && <ProductDetails product={selectedForDetails} onClose={() => setShowDetails(false)} getImageUrl={getImageUrl} />}
    </section>
  );
};

/* ---------------------------------------------------------------------------
   BELOW THIS ‚Äî ALL YOUR ORIGINAL COMPONENTS (UNCHANGED)
--------------------------------------------------------------------------- */

const FeaturedCategoryCard = React.memo(({ category, subProducts, imageSrc, onViewAll }) => (
  <motion.div className="flex flex-col overflow-hidden transition-all bg-white border border-gray-200 shadow-lg rounded-xl hover:shadow-2xl" whileHover={{ y: -8 }}>
    <div className="relative flex-shrink-0 h-48 mb-4 overflow-hidden rounded-t-xl bg-gradient-to-br from-blue-50 to-gray-100">
      {imageSrc ? (
        <img src={imageSrc} className="object-cover w-full h-full transition-transform duration-500 hover:scale-110" />
      ) : (
        <div className="flex items-center justify-center w-full h-full">No Preview</div>
      )}
      <div className="absolute px-3 py-1 text-sm font-semibold text-white bg-blue-600 rounded-full top-2 right-2">
        {subProducts.length} Items
      </div>
    </div>

    <div className="flex flex-col flex-grow p-6">
      <h3 className="mb-3 text-xl font-bold text-gray-900">{category}</h3>
      <ul className="flex-grow mb-4 space-y-2 text-sm text-gray-600">
        {subProducts.slice(0, 5).map((sub, idx) => (
          <li key={idx}>{sub.name}</li>
        ))}
      </ul>
      <button onClick={onViewAll} className="self-start px-4 py-2 font-medium text-blue-600 border border-blue-300 rounded-lg">
        View All Products ‚Üí
      </button>
    </div>
  </motion.div>
));

/* -------------------- ProductCard (unchanged) -------------------- */
const ProductCard = React.memo(
  ({ name, price, images, description, specs, additionalInfo, onQuote, onDetails, onCompare, isCompared, getImageUrl }) => (
    <motion.div className="flex flex-col p-6 transition-all bg-white border border-gray-200 shadow-lg rounded-xl hover:shadow-2xl" whileHover={{ y: -8 }}>
      <div className="relative h-48 mb-4 overflow-hidden bg-gray-100 rounded-lg">
        {images?.[0] ? (
          <img src={getImageUrl(images[0])} className="object-cover w-full h-full" />
        ) : (
          <div className="flex items-center justify-center w-full h-full">No Image</div>
        )}
      </div>
      <h3 className="mb-2 text-lg font-bold">{name}</h3>
      {price && <p className="mb-3 text-xl font-bold text-green-600">{price}</p>}
      <p className="mb-4 text-sm text-gray-600">{description}</p>

      <div className="flex gap-2 mt-auto">
        <button onClick={onQuote} className="flex-1 px-4 py-2 text-white bg-blue-600 rounded-lg">
          Get Quote
        </button>
        <button onClick={onDetails} className="flex-1 px-4 py-2 text-gray-700 bg-gray-200 rounded-lg">
          Details
        </button>
      </div>
      <button
        onClick={onCompare}
        className={`w-full px-4 py-2 mt-2 rounded-lg ${
          isCompared ? "bg-blue-100 border-blue-600 border-2 text-blue-700" : "border border-gray-300"
        }`}
      >
        {isCompared ? "Added to Compare" : "+ Compare"}
      </button>
    </motion.div>
  )
);

/* -------------------- Compare Panel (unchanged) -------------------- */
const ComparePanel = React.memo(({ products, onRemove }) => (
  <motion.div className="fixed p-6 bg-white border-2 border-blue-600 shadow-2xl bottom-4 right-4 rounded-xl">
    <h4 className="mb-4 text-lg font-bold">Compare Products ({products.length}/4)</h4>
    {products.map((p, i) => (
      <div key={i} className="flex justify-between mb-3">
        <span>{p.name}</span>
        <button onClick={() => onRemove(p)} className="text-red-600">Remove</button>
      </div>
    ))}
  </motion.div>
));

/* -------------------- Product Details (unchanged) -------------------- */
const ProductDetails = ({ product, onClose, getImageUrl }) => {
  if (!product) return null;

  const [index, setIndex] = useState(0);

  return (
    <div className="fixed inset-0 flex items-center justify-center p-4 bg-black/50" onClick={onClose}>
      <motion.div className="w-full max-w-3xl p-6 bg-white rounded-xl" onClick={(e) => e.stopPropagation()}>
        <button onClick={onClose} className="float-right">‚úï</button>
        <img src={getImageUrl(product.images[index])} className="object-cover w-full h-80" />

        <h3 className="mt-4 text-2xl font-bold">{product.name}</h3>
        <p className="mt-3 text-gray-700">{product.description}</p>
      </motion.div>
    </div>
  );
};

/* -------------------- Quote Form (unchanged logic) -------------------- */
const QuoteForm = ({ product, onClose }) => {
  const [form, setForm] = useState({ name: "", email: "", phone: "", details: "" });

  return (
    <div className="fixed inset-0 flex items-center justify-center bg-black/40" onClick={onClose}>
      <div className="w-full max-w-sm p-6 bg-white rounded-xl" onClick={(e) => e.stopPropagation()}>
        <button onClick={onClose} className="float-right">‚úï</button>
        <h3 className="mb-4 text-xl font-bold">Get Quote ‚Äì {product?.name}</h3>

        <input type="text" placeholder="Name" className="w-full p-2 mb-3 border rounded" />
        <input type="email" placeholder="Email" className="w-full p-2 mb-3 border rounded" />
        <input type="tel" placeholder="Phone" className="w-full p-2 mb-3 border rounded" />
        <textarea className="w-full p-2 border rounded" placeholder="Details"></textarea>

        <button className="w-full py-2 mt-4 text-white bg-blue-600 rounded-md">Submit</button>
      </div>
    </div>
  );
};

export default Products;
