import React, { useState, useEffect } from "react";
import { motion } from "framer-motion";

const Contact = () => {
  const [formData, setFormData] = useState({
    name: "",
    mobile: "",
    email: "",
    message: "",
  });

  const [status, setStatus] = useState("");

  // === EMAILJS CONFIG (working) ===
  const EMAILJS_KEY = "LdiT36053mIIantaE";       // Public Key
  const EMAILJS_SERVICE = "service_";     // Gmail service
  const EMAILJS_TEMPLATE = "template_9lqtcc5";   // Contact template

  // Load EmailJS script (only once)
  useEffect(() => {
    if (window.emailjs) {
      window.emailjs.init(EMAILJS_KEY);
      return;
    }

    const script = document.createElement("script");
    script.src = "https://cdn.jsdelivr.net/npm/@emailjs/browser@4/dist/email.min.js";
    script.async = true;
    script.onload = () => {
      window.emailjs.init(EMAILJS_KEY);
      console.log("EmailJS Loaded & Ready!");
    };
    document.body.appendChild(script);
  }, []);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setStatus("sending");

    try {
      if (!window.emailjs) throw new Error("EmailJS not loaded");

      await window.emailjs.send(EMAILJS_SERVICE, EMAILJS_TEMPLATE, {
        from_name: formData.name,
        from_email: formData.email,
        phone: formData.mobile,
        message: formData.message,
      });

      setStatus("success");
      setFormData({ name: "", mobile: "", email: "", message: "" });

    } catch (err) {
      console.error("Email Send Failed → ", err);
      setStatus("error");
    }

    setTimeout(() => setStatus(""), 5000);
  };

  return (
    <section id="contact" className="py-12 sm:py-20 bg-navy-50">
      <div className="px-4 mx-auto max-w-7xl">
        <motion.h2
          className="mb-8 text-3xl font-bold text-center sm:text-5xl sm:mb-12 text-navy-800"
          initial={{ y: -50 }}
          whileInView={{ y: 0 }}
          viewport={{ once: true }}
        >
          Contact Us
        </motion.h2>

        <motion.div
          className="grid grid-cols-1 gap-6 md:grid-cols-2 md:gap-12"
          initial={{ opacity: 0 }}
          whileInView={{ opacity: 1 }}
          viewport={{ once: true }}
        >
          {/* Contact Details */}
          <div className="space-y-4 text-navy-700">
            <h3 className="text-xl font-bold sm:text-2xl">Details</h3>

            <p><strong>Email:</strong> indiaadvertising05@gmail.com</p>
            <p><strong>Phone:</strong> +91 9899572539</p>
            <p><strong>Address:</strong> Plot No. H-27 Sector-9 Noida</p>
          </div>

          {/* Form */}
          <form onSubmit={handleSubmit} className="space-y-4">
            <input
              type="text"
              name="name"
              required
              placeholder="Your Name"
              value={formData.name}
              onChange={handleChange}
              className="w-full p-3 border rounded border-navy-300"
            />

            <input
              type="tel"
              name="mobile"
              required
              placeholder="Mobile No."
              value={formData.mobile}
              onChange={handleChange}
              className="w-full p-3 border rounded border-navy-300"
            />

            <input
              type="email"
              name="email"
              required
              placeholder="Email Address"
              value={formData.email}
              onChange={handleChange}
              className="w-full p-3 border rounded border-navy-300"
            />

            <textarea
              name="message"
              rows="4"
              required
              placeholder="Your Message"
              value={formData.message}
              onChange={handleChange}
              className="w-full p-3 border rounded border-navy-300"
            ></textarea>

            <motion.button
              type="submit"
              disabled={status === "sending"}
              className={`w-full py-4 font-bold rounded ${
                status === "sending"
                  ? "bg-gray-400"
                  : "bg-yellow-500 hover:bg-yellow-600"
              }`}
            >
              {status === "sending" ? "Sending..." : "Send Message"}
            </motion.button>

            {status === "success" && (
              <div className="p-3 text-green-700 bg-green-100 border-l-4 border-green-600">
                Message sent successfully!
              </div>
            )}

            {status === "error" && (
              <div className="p-3 text-red-700 bg-red-100 border-l-4 border-red-600">
                Failed to send. Please try again.
              </div>
            )}
          </form>
        </motion.div>
      </div>
    </section>
  );
};

export default Contact;
